helptexts = {} function HelpMenu()local Menu = vgui.Create("DFrame")Menu:SetPos(ScrW() / 2-400,ScrH() / 2-320)Menu:SetSize(800, 640)Menu:SetTitle("Help Menu")Menu:SetDraggable(true)Menu:ShowCloseButton(true)Menu:SetVisible(true)Menu:MakePopup()Basics = vgui.Create("DPanel")Basics:SetSize(780,580)Basics:SetBackgroundColor(Color(60,60,60,255))local BasicsText = vgui.Create("DLabel",Basics)BasicsText:SetPos(10,10)BasicsText:SetText( helptexts.basics )BasicsText:SetWidth(400)BasicsText:SetTall(560)BasicsText:SetFont("DungeonS")BasicsText:SetWrap(true)local TreeB = vgui.Create( "DModelPanel", Basics )TreeB:SetModel( "models/pg_props/pg_forest/pg_birch.mdl" ) TreeB:SetPos(420,100)TreeB:SetSize( 340, 340 )TreeB:SetCamPos( Vector( 300, 300, 180 ) )TreeB:SetLookAt( Vector( 0, 0, 180 ) )Mining = vgui.Create("DPanel")Mining:SetSize(780,580)Mining:SetBackgroundColor(Color(60,60,60,255))local MRocks = vgui.Create( "DPanelList" , Mining )MRocks:EnableVerticalScrollbar( true )MRocks:EnableHorizontal( false )MRocks:SetSpacing( 5 )MRocks:SetPadding( 5 )MRocks:SetPos(420,10)MRocks:SetSize(340,260)local OreM = vgui.Create( "DModelPanel", Mining )OreM:SetModel( "models/PG_props/pg_forest/pg_ore_all.mdl" ) OreM:SetPos(440,260)OreM:SetSize( 320, 320 )OreM:SetCamPos( Vector( 60, 60, 80 ) )OreM:SetLookAt( Vector( 0, 0, 0 ) )local SOres = SortArray(Ores,"level")for i=1,table.Count(SOres) do		local RockP = vgui.Create("DPanel")	RockP:SetSize(360,80)	RockP:SetBackgroundColor(Color(80,80,80,255))	local model = GetIModel(Ores[SOres[i]].res)	local skin = GetISkin(Ores[SOres[i]].res)		local RockI = vgui.Create( "SpawnIcon", RockP )	RockI:SetModel( model,skin )	RockI:SetSize( 60, 60 )	RockI:SetPos(10,10)	RockI:SetToolTip(GetIName(Ores[SOres[i]].res))		local Rocklvl = vgui.Create("DLabel",RockP)	Rocklvl:SetPos(80,20)	Rocklvl:SetText( GetIName(Ores[SOres[i]].res) .. " - LvL : " .. Ores[SOres[i]].level)	Rocklvl:SetWidth(400)	Rocklvl:SetTall(40)	Rocklvl:SetFont("Adventure")	MRocks:AddItem(RockP)endlocal MiningText = vgui.Create("DLabel",Mining)MiningText:SetPos(10,10)MiningText:SetText( helptexts.mining )MiningText:SetWidth(400)MiningText:SetTall(560)MiningText:SetFont("DungeonS")MiningText:SetWrap(true)local Mobs = vgui.Create( "DPanelList" )Mobs:EnableVerticalScrollbar( true )Mobs:EnableHorizontal( false )Mobs:SetSpacing( 5 )Mobs:SetPadding( 5 )Mobs:SetPos(10,10)Mobs:SetSize(780,580)for k,v in pairs(MobInfo) do	local MobP = vgui.Create("DPanel")	MobP:SetSize(770,80)	MobP:SetBackgroundColor(Color(80,80,80,255))	local name = MobInfo[k].name	if (MobInfo[k].aggressive) then name = name .. " [Aggressive]" end	local MName = vgui.Create("DLabel",MobP)	MName:SetPos(10,10)	MName:SetText( name )	MName:SetFont("Adventure")	MName:SetWidth(400)		local MStats = vgui.Create("DLabel",MobP)	MStats:SetPos(10,40)	MStats:SetText("Basic Stats : Atk/Def/Str/HP : " ..  MobInfo[k].attack .. "/" ..  MobInfo[k].defense .. "/" ..  MobInfo[k].strength .. "/" ..  MobInfo[k].health)	MStats:SetFont("AdventureS")	MStats:SetWidth(400)		local i = 0		for x = 1,table.Count(MobInfo[k].drops)/4 do		local item = MobInfo[k].drops[x*4-3]	local min = MobInfo[k].drops[x*4-2]	local max = MobInfo[k].drops[x*4-1]	local chance = MobInfo[k].drops[x*4]		local model = GetIModel(item)	local skin = GetISkin(item)		local MDrop = vgui.Create( "SpawnIcon", MobP )	MDrop:SetModel( model,skin )	MDrop:SetSize( 40, 40 )	MDrop:SetPos(400+i,20)	MDrop:SetToolTip(GetIName(item) .. "\nAmount : " .. min .. " - " .. max .. "\nChance : " .. chance .. "%")	i = i + 45		end		Mobs:AddItem(MobP)endlocal BossList = vgui.Create( "DPanelList" )BossList:EnableVerticalScrollbar( true )BossList:EnableHorizontal( false )BossList:SetSpacing( 5 )BossList:SetPadding( 5 )BossList:SetPos(10,10)BossList:SetSize(780,580)for k,v in pairs(Bosses) do	local BossP = vgui.Create("DPanel")	BossP:SetSize(770,80)	BossP:SetBackgroundColor(Color(80,80,80,255))	local name = Bosses[k].name	local BName = vgui.Create("DLabel",BossP)	BName:SetPos(10,10)	BName:SetText( name )	BName:SetFont("Adventure")	BName:SetWidth(400)		local BStats = vgui.Create("DLabel",BossP)	BStats:SetPos(10,35)	BStats:SetText("Basic Stats : Atk/Def/Str/HP : " ..  Bosses[k].attack .. "/" ..  Bosses[k].defense .. "/" ..  Bosses[k].strength .. "/" ..  Bosses[k].health)	BStats:SetFont("AdventureS")	BStats:SetWidth(400)		local BStats = vgui.Create("DLabel",BossP)	BStats:SetPos(10,55)	BStats:SetText("Token : " ..  Bosses[k].tamount .. " " ..  Tokens[Bosses[k].token])	BStats:SetFont("AdventureS")	BStats:SetWidth(400)		local i = 0		for x = 1,table.Count(Bosses[k].drops)/4 do		local item = Bosses[k].drops[x*4-3]	local min = Bosses[k].drops[x*4-2]	local max = Bosses[k].drops[x*4-1]	local chance = Bosses[k].drops[x*4]		local model = GetIModel(item)	local skin = GetISkin(item)		local BDrop = vgui.Create( "SpawnIcon", BossP )	BDrop:SetModel( model,skin )	BDrop:SetSize( 40, 40 )	BDrop:SetPos(400+i,20)	BDrop:SetToolTip(GetIName(item) .. "\nAmount : " .. min .. " - " .. max .. "\nChance : " .. chance .. "%")	i = i + 45		end		BossList:AddItem(BossP)endFood = vgui.Create("DPanel")Food:SetSize(780,580)Food:SetBackgroundColor(Color(60,60,60,255))local FoodText = vgui.Create("DLabel",Food)FoodText:SetPos(10,10)FoodText:SetText( helptexts.food )FoodText:SetWidth(400)FoodText:SetTall(560)FoodText:SetFont("DungeonS")FoodText:SetWrap(true)local FoodList = vgui.Create( "DPanelList" ,Food)FoodList:EnableVerticalScrollbar( true )FoodList:EnableHorizontal( false )FoodList:SetSpacing( 5 )FoodList:SetPadding( 5 )FoodList:SetPos(420,10)FoodList:SetSize(340,260)for k,v in pairs(Eatable) do	local FoodP = vgui.Create("DPanel")	FoodP:SetSize(330,80)	FoodP:SetBackgroundColor(Color(80,80,80,255))	local model = GetIModel(k)	local skin = GetISkin(k)	local FoodI = vgui.Create( "SpawnIcon", FoodP )	FoodI:SetModel( model,skin )	FoodI:SetSize( 60, 60 )	FoodI:SetPos(10,10)	FoodI:SetToolTip(GetIName(k))	local FoodN = vgui.Create("DLabel",FoodP)	FoodN:SetPos(80,10)	FoodN:SetText( GetIName(k) )	FoodN:SetFont("Adventure")	FoodN:SetWidth(400)	local FoodHu = vgui.Create("DLabel",FoodP)	FoodHu:SetPos(80,40)	FoodHu:SetText( "Restore Hunger : " .. Eatable[k].hunger )	FoodHu:SetFont("AdventureS")	FoodHu:SetWidth(400)	local FoodHp = vgui.Create("DLabel",FoodP)	FoodHp:SetPos(80,60)	FoodHp:SetText( "Restore Health : " .. Eatable[k].health )	FoodHp:SetFont("AdventureS")	FoodHp:SetWidth(400)		FoodList:AddItem(FoodP)endlocal CookList = vgui.Create( "DPanelList" ,Food)CookList:EnableVerticalScrollbar( true )CookList:EnableHorizontal( false )CookList:SetSpacing( 5 )CookList:SetPadding( 5 )CookList:SetPos(420,300)CookList:SetSize(340,260)SCook = SortArray(Cook,"lvl")for i=1,table.Count(SCook) do	local CookP = vgui.Create("DPanel")	CookP:SetSize(330,80)	CookP:SetBackgroundColor(Color(60,60,60,255))	local model = GetIModel(SCook[i])	local skin = GetISkin(SCook[i])	local CookI = vgui.Create( "SpawnIcon", CookP )	CookI:SetModel( model,skin )	CookI:SetSize( 60, 60 )	CookI:SetPos(10,10)	CookI:SetToolTip(GetIName(SCook[i]))	local CookN = vgui.Create("DLabel",CookP)	CookN:SetPos(80,10)	CookN:SetText( GetIName(SCook[i]) )	CookN:SetFont("Adventure")	CookN:SetWidth(400)	local res = ""		for k,v in pairs(Cook[SCook[i]].res) do		res = v .. " " .. k .. " "	end		local CookRes = vgui.Create("DLabel",CookP)	CookRes:SetPos(80,40)	CookRes:SetText( "Resources : " .. res)	CookRes:SetFont("AdventureS")	CookRes:SetWidth(400)	local CookLvL = vgui.Create("DLabel",CookP)	CookLvL:SetPos(80,60)	CookLvL:SetText( "Required level : " .. Cook[SCook[i]].lvl )	CookLvL:SetFont("AdventureS")	CookLvL:SetWidth(400)		CookList:AddItem(CookP)endlocal RuneList = vgui.Create( "DPanelList" )RuneList:EnableVerticalScrollbar( true )RuneList:EnableHorizontal( false )RuneList:SetSpacing( 5 )RuneList:SetPadding( 5 )RuneList:SetPos(10,10)RuneList:SetSize(780,580)for k,v in pairs(Runes) do	local RuneP = vgui.Create("DPanel")	RuneP:SetSize(770,80)	RuneP:SetBackgroundColor(Color(80,80,80,255))	local model = GetIModel(k)	local skin = GetISkin(k)	local Rune = vgui.Create( "SpawnIcon", RuneP )	Rune:SetModel( model,skin )	Rune:SetSize( 60, 60 )	Rune:SetPos(10,10)	Rune:SetToolTip(GetIName(k))	local RName = vgui.Create("DLabel",RuneP)	RName:SetPos(80,10)	RName:SetText( GetIName(k) )	RName:SetFont("Adventure")	RName:SetWidth(130)	local Orb = vgui.Create("DLabel",RuneP)	Orb:SetPos(80,40)	Orb:SetText( GetIName(Runes[k].orb) )	Orb:SetFont("AdventureS")	Orb:SetWidth(130)	local mtext = ""	for i=1,table.Count(Runes[k].multi) do		local level = 1		for k,v in pairs(Runes[k].multi) do			if (v == i) then level = k end		end		if (i < table.Count(Runes[k].multi)) then			mtext = mtext .. level .. " / "		else mtext = mtext .. level		end	end	local Multi = vgui.Create("DLabel",RuneP)	Multi:SetPos(210,40)	Multi:SetText("Multiplying : " .. mtext)	Multi:SetFont("AdventureS")	Multi:SetWidth(360)	RuneList:AddItem(RuneP)endRunecrafting = vgui.Create("DPanel")Runecrafting:SetSize(780,580)Runecrafting:SetBackgroundColor(Color(60,60,60,255))local RuneText = vgui.Create("DLabel",Runecrafting)RuneText:SetPos(10,10)RuneText:SetText( helptexts.runecrafting )RuneText:SetWidth(400)RuneText:SetTall(560)RuneText:SetFont("DungeonS")RuneText:SetWrap(true)local REss = vgui.Create( "DModelPanel", Runecrafting )REss:SetModel( "models/darkages/rune_ess_ore01.mdl" ) REss:SetPos(460,10)REss:SetSize( 280, 280 )REss:SetCamPos( Vector( 100, 100, 40 ) )REss:SetLookAt( Vector( 0, 0, 40 ) )local RAltair = vgui.Create( "DModelPanel", Runecrafting )RAltair:SetModel( "models/darkages/rune_ess_altar.mdl" ) RAltair:SetPos(460,300)RAltair:SetSize( 280, 280 )RAltair:SetCamPos( Vector( 100, 100, 160 ) )RAltair:SetLookAt( Vector( 0, 0, 0 ) )MenuSheet = vgui.Create( "DPropertySheet" )MenuSheet:SetParent( Menu )MenuSheet:SetPos(10,25)MenuSheet:SetSize(780,600)MenuSheet:AddSheet( "Basics",Basics,"gui/silkicons/user",false,false,"Basics")MenuSheet:AddSheet( "Mining",Mining,"gui/silkicons/user",false,false,"Mining")MenuSheet:AddSheet( "Woodcutting",Basics,"gui/silkicons/user",false,false,"Woodcutting")MenuSheet:AddSheet( "Smithing",Basics,"gui/silkicons/user",false,false,"Smithing")MenuSheet:AddSheet( "Crafting",Basics,"gui/silkicons/user",false,false,"Crafting")MenuSheet:AddSheet( "Food",Food,"gui/silkicons/user",false,false,"Food")MenuSheet:AddSheet( "Magic",Basics,"gui/silkicons/user",false,false,"Magic")MenuSheet:AddSheet( "Runes",RuneList,"gui/silkicons/user",false,false,"Runes")MenuSheet:AddSheet( "Runecrafting",Runecrafting,"gui/silkicons/user",false,false,"Runecrafting")MenuSheet:AddSheet( "Combat",Basics,"gui/silkicons/user",false,false,"Combat")MenuSheet:AddSheet( "Mobs",Mobs,"gui/silkicons/user",false,false,"Mobs")MenuSheet:AddSheet( "Bosses",BossList,"gui/silkicons/user",false,false,"Bosses")endusermessage.Hook("helpmenu",HelpMenu)function HelpText()local data = net.ReadTable()helptexts = dataHelpMenu()endnet.Receive("helptexts",HelpText)