local helptexts = {}helptexts.basics = file.Read("data/darkages/help/basics.txt","GAME")helptexts.mining = file.Read("data/darkages/help/mining.txt","GAME")helptexts.food = file.Read("data/darkages/help/food.txt","GAME")helptexts.runecrafting = file.Read("data/darkages/help/runecrafting.txt","GAME")function HelpSpawn(ply)	timer.Simple(1, function()	net.Start("helptexts")		net.WriteTable(helptexts)	net.Send(ply)		end)endhook.Add("PlayerInitialSpawn","HelpSpawn",HelpSpawn)function HelpSay(ply,text)	if (string.sub(text,0,5) == "/help") then			net.Start("helptexts")			net.WriteTable(helptexts)		net.Send(ply)			return false	endendhook.Add("PlayerSay","HelpSay",HelpSay)