qtracks = {}stracks = {}if (file.Exists("darkages/settings.txt", "DATA")) thenlocal x = {}x = util.KeyValuesToTable(file.Read("darkages/settings.txt", "DATA"))qtracks = x.qtracksstracks = x.strackselselocal x = {}x.qtracks = qtracksx.stracks = stracksfile.Write("darkages/settings.txt",util.TableToKeyValues(x))endfunction QuestHud()	local i = 0	if (qtracks == nil) then return end	if (plydata == nil or plydata.inventory == nil) then return end	for k,v in pairs(qtracks) do			draw.RoundedBox( 3, ScrW()-210, 100 + i, 200,table.Count(Quests[k].requirements) * 20 + 20, Color(0,0,0,128))		draw.DrawText(Quests[k].title,"Knight",ScrW()-205,105 + i)				local g = 0				for k,v in pairs(Quests[k].requirements) do					if (plydata.inventory[k] == nil) then							item = 0						else							item = plydata.inventory[k] 						end						draw.DrawText(GetIName(k) .. " " .. item .." / " .. v,"DungeonS",ScrW()-205, 125 + i + g*20)						g = g + 1				end				i = i + table.Count(Quests[k].requirements) * 20 + 20 + 10			endendhook.Add("HUDPaint","QHud",QuestHud)