--Staminafunction Stamina_Spawn(ply)	timer.Create(ply:UniqueID().."stamina_down",0.5,0,function()			if (ply.Stamina == 0) then					timer.Stop(ply:UniqueID().."stamina_down")			ply:SetRunSpeed(210 - ply.data.iweight * 2)				else					ply.Stamina = ply.Stamina - 1					if (ply:WaterLevel() > 0) then							ply:SetRunSpeed(230 - ply.data.iweight * 2)						else							ply:SetRunSpeed(290 - ply.data.iweight * 2)						end				end				ply:SetNWInt("stamina",ply.Stamina)					end)		timer.Create(ply:UniqueID().."stamina_up",1,0,function()			if (ply.Stamina == 100) then					timer.Stop(ply:UniqueID().."stamina_up")				else						ply.Stamina = ply.Stamina + 1				end				ply:SetNWInt("stamina",ply.Stamina)			end)timer.Stop(ply:UniqueID().."stamina_down")timer.Stop(ply:UniqueID().."stamina_up")timer.Stop(ply:UniqueID().."stamina_duck")endhook.Add("PlayerSpawn","Stamina_Spawn",Stamina_Spawn)		function Running_Start(ply,key)	if (key == IN_SPEED) then			timer.Start(ply:UniqueID().."stamina_down")	timer.Stop(ply:UniqueID().."stamina_up")	timer.Stop(ply:UniqueID().."stamina_duck")		endendhook.Add("KeyPress","Running_Start",Running_Start)function Running_Stop(ply,key)	if (key == IN_SPEED) then		timer.Start(ply:UniqueID().."stamina_up")	timer.Stop(ply:UniqueID().."stamina_down")		endendhook.Add("KeyRelease","Running_Stop",Running_Stop)function SetBaseStamina(ply)local stamina = 100ply.Stamina = staminaply:SetNWInt("stamina",ply.Stamina)endhook.Add("PlayerSpawn","SetBaseStamina",SetBaseStamina)